import { useState, Component } from 'react'

export default class Users extends Component {
    constructor(props) {
        super(props)
        const names = ['Kiryu Kazuma', 'Goro Majima', 'Masayoshi Tanimura', 'Akira Nishikiyama', 'Haruka Sawamura', 'Makoto Date', 'Shintaro Kazama', 'Ichiban Kasuga', 'Shun Akiyama']
        const itemsPerList = 4
        const listsOfNames = new Array(Math.ceil(names.length / itemsPerList))
            .fill()
            .map(_ => names.splice(0, itemsPerList))
        this.state = {
            rows: listsOfNames.map((list) => { return <ul style={{ display: 'flex' }}> { list.map((name) => { return <li style={{ listStyleType: 'none', background: '#C9DDFF', margin: '10px', padding: '5%' }}><h2>{ name }</h2></li>}) } </ul>})
        }
        
    }
    render() {
        return (
            <div style={{ margin: 'auto auto', }}>
                <h1>Usu√°rios Cadastrados</h1>
                { this.state.rows }
            </div>
        )
    }
}
