import { useState, Component } from 'react'

export default class Users extends Component {
    constructor(props) {
        super(props)
        const names = ['Kiryu Kazuma', 'Goro Majima', '']
        const itemsPerList = 4
        const listsOfNames = new Array(Math.ceil(names.length / itemsPerList))
            .fill()
            .map(_ => names.splice(0, itemsPerList))
        this.state = {
            rows: listsOfNames.map((list) => { return <ul> { list.map((name) => { return <li>{ name }</li>}) } </ul>})
        }
        
    }
    render() {
        return (
            <div style={{ margin: '0'}}>
                <h1>Usu√°rios Cadastrados</h1>
                { this.state.rows }
            </div>
        )
    }
}
