import { useEffect, useState, Component } from 'react';

export default class Field extends Component {
    constructor(props) {
        super(props)
        this.state = {
            values: [] // Nome, Email, Senha
        }
        this.props = { 
            fieldNames: ['Nome de usuário', 'Email', 'Senha']
        }
        

    }
    
    render() {
        return (
            <div>
                <fieldset>
                    <NameForm />
                    
                </fieldset>
            </div>
        )         

    }
}

class NameForm extends Component {
    constructor(props) {
        super(props)
        this.state = {value: ''}

        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(Event) {
        this.setState({value: Event.target.value})
    }

    render() {
        return(
            <form>
                <label>
                    Nome: 
                    <input type="text" value={ this.state.value } onChange={ this.handleChange }/>
                </label>
            </form>
        )
    }
}

class EmailForm extends Component {
    constructor(props) {
        super(props)
        this.state = {value: ''}

        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(Event) {
        this.setState({value: Event.target.value})
    }

    render() {
        return(
            <form>
                <label>
                    Email: 
                    <input type="text" value={ this.state.value } onChange={ this.handleChange }/>
                </label>
            </form>
        )
    }
}

class PasswordForm extends Component {
    constructor(props) {
        super(props)
        this.state = {value: ''}

        this.handleChange = this.handleChange.bind(this)
    }

    handleChange(Event) {
        this.setState({value: Event.target.value})
    }

    render() {
        return(
            <form>
                <label>
                    Senha: 
                    <input type="text" value={ this.state.value } onChange={ this.handleChange }/>
                </label>
            </form>
        )
    }
}
